import { useState, useEffect, useRef } from "react";

type point = {
  x: number;
  y: number;
};

type useDragProps = {
  onDragStart: (e: React.MouseEvent) => void;
  onDrag: (e: React.MouseEvent) => void;
  onDragEnd: (e: React.MouseEvent) => void;
};

export const useDrag = ({ onDragStart, onDrag, onDragEnd }: useDragProps) => {
  let [isDragging, setIsDragging] = useState(false);
  let [isMousePressed, setMousePressed] = useState(false);
  let [mousePos, setMousePos] = useState<point | null>(null);

  const onMouseDown = (e: React.MouseEvent<any>) => {
    setMousePressed(true);
  };

  const onMouseMove = (e: React.MouseEvent<any>) => {
    if (!isMousePressed) return;
    if (isMousePressed && !isDragging) {
      onDragStart(e);
    }

    onDrag(event);
  };

  const mouseup = (e: MouseEvent) => {
    if (!isDragging || !startPos) return;
    const bbox = (containerRef.current as Element).getBoundingClientRect();
    const event = {
      clientX: e.clientX,
      clientY: e.clientY,
      x: e.clientX - bbox.left,
      y: e.clientY - bbox.top,
      deltaX: e.clientX - (mousePos as point).x,
      deltaY: e.clientY - (mousePos as point).y,
      clientStartPos: startPos,
      startPos: {
        x: startPos.x - bbox.left,
        y: startPos.y - bbox.top
      }
    };
    setStartPos(null);
    setMousePos(null);
    setIsDragging(false);
    onDragEnd(event);
  };

  useEffect(() => {
    document.addEventListener("mouseup", mouseup);
    return () => document.removeEventListener("mouseup", mouseup);
  });

  return {
    ref: containerRef,
    onMouseDown,
    onMouseMove
  };
};
